mapper.insert:
SELECT GET_MGRNU('GT_C100_P') FROM DUAL
INSERT INTO GT_C100_P ( MGRNU, CTK_MGRNU, CSS_NAM, PE_NAM, GU_NAM, DONG_NAM, SCH_NAM, MINX, MINY, MAXX, MAXY, XCE, YCE, PE_CDE, GU_CDE, DONG_CDE, CSS_MGRNU, SCH_MGRNU, RI_NAM, RI_CDE, JIBUN, PNU, XGEO, ROD_NAM, ROD_MGRNU, EVE, GBN, REG_YMD
) VALUES ( #{mgrnu}, NULL, #{cssNam}, #{peNam}, #{guNam}, #{dongNam}, #{schNam}, #{minX}, #{minY}, #{maxX}, #{maxY}, #{xce}, #{yce}, #{peCde}, #{guCde}, #{dongCde}, #{cssMgrnu}, #{schMgrnu}, #{liNam}, #{liCde}, #{jibun}, #{pnu}, MDSYS.SDO_GEOMETRY(#{geom}, 5181), #{rodNam}, #{rodMgrnu}, #{eve}, #{gbn}, SYSDATE
)

mapper.update:
UPDATE GT_C100_P_WORK
SET WORKTM = #{worktm},
	WORKUSER = #{workuser},
	WORK_CDE = #{workCde},
	WORK_BG = #{workBg}
WHERE MGRNU = #{mgrnu} AND WORK_ACTIVE = 1 AND MGRNU_IDX = #{mgrnuIdx}

UPDATE GT_C100_P_WORK
SET WORK_ACTIVE = 0
WHERE MGRNU = #{mgrnu} AND MGRNU_IDX = #{mgrnuIdx}
