SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "인입선", TO_CHAR(A02, '999,999,999,999') AS "신호선", TO_CHAR(A03, '999,999,999,999') AS "통신선", TO_CHAR(A04, '999,999,999,999') AS "조명등선" FROM ( SELECT A.GU_NAM AS NAME, (COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '1', 1, NULL)) AS A01, COUNT(DECODE(KND, '2', 1, NULL)) AS A02, COUNT(DECODE(KND, '3', 1, NULL)) AS A03, COUNT(DECODE(KND, '4', 1, NULL)) AS A04 FROM SMARTTGIS.GT_A078_L A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.LINE_MET IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(A.GU_NAM) ORDER BY NAME NULLS FIRST) TEMP

SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "인입선", TO_CHAR(A02, '999,999,999,999') AS "신호선", TO_CHAR(A03, '999,999,999,999') AS "통신선", TO_CHAR(A03, '999,999,999,999') AS "조명등선" FROM ( SELECT A.PE_NAM AS NAME, (COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '1', 1, NULL)) AS A01, COUNT(DECODE(KND, '2', 1, NULL)) AS A02, COUNT(DECODE(KND, '3', 1, NULL)) AS A03, COUNT(DECODE(KND, '4', 1, NULL)) AS A04 FROM SMARTTGIS.GT_A078_L A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.LINE_MET IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(A.PE_NAM) ORDER BY NAME NULLS FIRST) TEMP

SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "인입선", TO_CHAR(A02, '999,999,999,999') AS "신호선", TO_CHAR(A03, '999,999,999,999') AS "통신선", TO_CHAR(A04, '999,999,999,999') AS "조명등선" FROM ( SELECT G.EMD_KOR_NM AS NAME, (COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '1', 1, NULL)) AS A01, COUNT(DECODE(KND, '2', 1, NULL)) AS A02, COUNT(DECODE(KND, '3', 1, NULL)) AS A03, COUNT(DECODE(KND, '4', 1, NULL)) AS A04 FROM SMARTTGIS.GT_A078_L A, SMARTTGIS.GT_LP_AA_EMD G WHERE A.DONG_NAM(+) = G.EMD_KOR_NM
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.LINE_MET IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(G.EMD_KOR_NM) ORDER BY NAME NULLS FIRST) TEMP


		SELECT  TEMP.*
		FROM   (SELECT  ROWNUM  AS NUM,
		               TEMP.*,
		                COUNT(*) OVER() AS TOTALROWS
		        FROM   (SELECT
								 		MGRNU,
                                         PE_NAM,
                                         GU_NAM,
                                         DONG_NAM,
                                         JIBUN,
                                         CSS_NAM,
                                         TO_CODE('A078_L','KND',KND) AS KND_NAM,
                                         TO_CODE('A078_L','LINE_MET',LINE_MET) AS LINE_MET_NAM,
		                                TO_CHAR(ESB_YMD ,'YYYY-MM-DD') AS ESB_YMD
						FROM	SMARTTGIS.GT_A078_L A



				AND A.GU_CDE  IN 	#{item}


				AND A.DONG_CDE  IN 	#{item}


				AND A.PE_CDE  IN 	#{item}


				AND A.KND  IN 	#{item}


				AND A.LINE_MET  IN 	#{item}


				AND A.ESB_YMD >= #{stYmd}


				AND A.ESB_YMD <= #{enYmd}



		                ORDER BY MGRNU DESC
		                ) TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}


