SELECT TEMP.* FROM (SELECT ROWNUM AS NUM, TEMP.*, COUNT(*) OVER() AS TOTALROWS FROM ( SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "인입선", TO_CHAR(A02, '999,999,999,999') AS "신호선", TO_CHAR(A03, '999,999,999,999') AS "통신선", TO_CHAR(A04, '999,999,999,999') AS "조명등선" FROM ( SELECT A.CSS_NAM AS NAME, (COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '1', 1, NULL)) AS A01, COUNT(DECODE(KND, '2', 1, NULL)) AS A02, COUNT(DECODE(KND, '3', 1, NULL)) AS A03, COUNT(DECODE(KND, '3', 1, NULL)) AS A04 FROM SMARTTGIS.GT_A078_L A WHERE A.CSS_NAM IS NOT NULL
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.LINE_MET IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
AND A.CSS_NAM LIKE '%' || #{cssNam} || '%'
GROUP BY ROLLUP(A.CSS_NAM) ORDER BY NAME NULLS FIRST ) TEMP)TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}
