SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(LEN, '999,999,999,999') AS "연장(km)" FROM ( SELECT A.GU_NAM AS NAME, COUNT(*) AS TOTAL, SUM(LEN) AS LEN FROM GT_A079_L A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(A.GU_NAM) ORDER BY NAME NULLS FIRST) TEMP


    	SELECT
			DECODE(NAME, NULL, '합계', NAME) AS  "구분",
			TO_CHAR(TOTAL, '999,999,999,999') AS "합계",
			TO_CHAR(LEN, '999,999,999,999') AS "연장(km)"
		FROM  (
		SELECT  A.PE_NAM AS NAME,
				COUNT(*) AS TOTAL,
				SUM(LEN) AS LEN
		FROM GT_A079_L A



		AND A.GU_CDE  IN 	#{item}


		AND A.DONG_CDE  IN 	#{item}


		AND A.PE_CDE  IN 	#{item}


		AND A.ESB_YMD >= #{stYmd}


		AND A.ESB_YMD <= #{enYmd}



		GROUP BY ROLLUP(A.PE_NAM)
		ORDER BY NAME NULLS FIRST) TEMP

SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(LEN, '999,999,999,999') AS "연장(km)" FROM ( SELECT G.EMD_KOR_NM AS NAME, COUNT(*) AS TOTAL, SUM(LEN) AS LEN FROM GT_A079_L A, GT_LP_AA_EMD G WHERE A.DONG_NAM(+) = G.EMD_KOR_NM
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(G.EMD_KOR_NM) ORDER BY NAME NULLS FIRST) TEMP


		SELECT  TEMP.*
		FROM   (SELECT  ROWNUM  AS NUM,
		               TEMP.*,
		                COUNT(*) OVER() AS TOTALROWS
		        FROM   (SELECT
								 		MGRNU,
                                         PE_NAM,
                                         GU_NAM,
                                         DONG_NAM,
                                         JIBUN,
                                         CSS_NAM,
		                                TO_CHAR(ESB_YMD ,'YYYY-MM-DD') AS ESB_YMD
						FROM	GT_A079_L A



				AND A.GU_CDE  IN 	#{item}


				AND A.DONG_CDE  IN 	#{item}


				AND A.PE_CDE  IN 	#{item}


				AND A.ESB_YMD >= #{stYmd}


				AND A.ESB_YMD <= #{enYmd}



		                ORDER BY MGRNU DESC
		                ) TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}

SELECT ROWNUM AS NUM, TEMP.* FROM (SELECT MGRNU, PE_NAM, GU_NAM, DONG_NAM, JIBUN, CSS_NAM, TO_CHAR(ESB_YMD ,'YYYY-MM-DD') AS ESB_YMD FROM GT_A079_L A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
ORDER BY MGRNU DESC) TEMP

SELECT TEMP.* FROM (SELECT ROWNUM AS NUM, TEMP.*, COUNT(*) OVER() AS TOTALROWS FROM ( SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(LEN, '999,999,999,999') AS "연장(km)" FROM ( SELECT A.CSS_NAM AS NAME, COUNT(*) AS TOTAL, SUM(LEN) AS LEN FROM GT_A079_L A WHERE A.CSS_NAM IS NOT NULL
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
AND A.CSS_NAM LIKE '%' || #{cssNam} || '%'
GROUP BY ROLLUP(A.CSS_NAM) ORDER BY NAME NULLS FIRST ) TEMP)TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}

SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(LEN, '999,999,999,999') AS "연장(km)" FROM ( SELECT A.CSS_NAM AS NAME, COUNT(*) AS TOTAL, SUM(LEN) AS LEN FROM GT_A079_L A WHERE A.CSS_NAM IS NOT NULL
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
AND A.CSS_NAM LIKE '%' || #{cssNam} || '%'
GROUP BY ROLLUP(A.CSS_NAM) ORDER BY NAME NULLS FIRST) TEMP
