SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "가드레일", TO_CHAR(A02, '999,999,999,999') AS "가드파이프", TO_CHAR(A03, '999,999,999,999') AS "박스형 보", TO_CHAR(A04, '999,999,999,999') AS "가드케이블", TO_CHAR(A05, '999,999,999,999') AS "강성방호울타리", TO_CHAR(A06, '999,999,999,999') AS "중앙탄력분리대", TO_CHAR(A07, '999,999,999,999') AS "기타" FROM ( SELECT G.EMD_KOR_NM AS NAME, (COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL)) + COUNT(DECODE(KND, '5', 1, NULL)) + COUNT(DECODE(KND, '6', 1, NULL)) + COUNT(DECODE(KND, '7', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '1', 1, NULL)) AS A01, COUNT(DECODE(KND, '2', 1, NULL)) AS A02, COUNT(DECODE(KND, '3', 1, NULL)) AS A03, COUNT(DECODE(KND, '4', 1, NULL)) AS A04, COUNT(DECODE(KND, '5', 1, NULL)) AS A05, COUNT(DECODE(KND, '6', 1, NULL)) AS A06, COUNT(DECODE(KND, '7', 1, NULL)) AS A07 FROM GT_C059_A A, GT_LP_AA_EMD G WHERE A.DONG_NAM(+) = G.EMD_KOR_NM
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.FNE_TYPE IN #{item}
AND A.CLR IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(G.EMD_KOR_NM) ORDER BY NAME NULLS FIRST) TEMP


SELECT	TEMP.*
FROM	(SELECT	ROWNUM  AS NUM,
				TEMP.*,
				COUNT(*) OVER() AS TOTALROWS
        FROM   (SELECT
					MGRNU,
					PE_NAM,
					GU_NAM,
					DONG_NAM,
					JIBUN,
					CSS_NAM,
					TO_CODE('C059_A','KND',KND) AS KND_NAM
				FROM	GT_C059_A A



		AND A.GU_CDE  IN 	#{item}


		AND A.DONG_CDE  IN 	#{item}


		AND A.PE_CDE  IN 	#{item}


		AND A.KND  IN 	#{item}


		AND A.FNE_TYPE  IN 	#{item}


		AND A.CLR  IN 	#{item}


		AND A.ESB_YMD >= #{stYmd}


		AND A.ESB_YMD <= #{enYmd}



                ORDER BY MGRNU DESC
                ) TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}


	SELECT	ROWNUM  AS NUM,
			TEMP.*
	FROM	(SELECT
					MGRNU,
					PE_NAM,
					GU_NAM,
					DONG_NAM,
					JIBUN,
					CSS_NAM,
					TO_CODE('C059_A','KND',KND) AS KND_NAM
			  FROM	GT_C059_A A



		AND A.GU_CDE  IN 	#{item}


		AND A.DONG_CDE  IN 	#{item}


		AND A.PE_CDE  IN 	#{item}


		AND A.KND  IN 	#{item}


		AND A.FNE_TYPE  IN 	#{item}


		AND A.CLR  IN 	#{item}


		AND A.ESB_YMD >= #{stYmd}


		AND A.ESB_YMD <= #{enYmd}



                ORDER BY MGRNU DESC) TEMP

SELECT TEMP.* FROM (SELECT ROWNUM AS NUM, TEMP.*, COUNT(*) OVER() AS TOTALROWS FROM (SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "가드레일", TO_CHAR(A02, '999,999,999,999') AS "가드파이프", TO_CHAR(A03, '999,999,999,999') AS "박스형 보", TO_CHAR(A04, '999,999,999,999') AS "가드케이블", TO_CHAR(A05, '999,999,999,999') AS "강성방호울타리", TO_CHAR(A06, '999,999,999,999') AS "중앙탄력분리대", TO_CHAR(A07, '999,999,999,999') AS "기타" FROM (SELECT A.CSS_NAM AS NAME, (COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL)) + COUNT(DECODE(KND, '5', 1, NULL)) + COUNT(DECODE(KND, '6', 1, NULL)) + COUNT(DECODE(KND, '7', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '1', 1, NULL)) AS A01, COUNT(DECODE(KND, '2', 1, NULL)) AS A02, COUNT(DECODE(KND, '3', 1, NULL)) AS A03, COUNT(DECODE(KND, '4', 1, NULL)) AS A04, COUNT(DECODE(KND, '5', 1, NULL)) AS A05, COUNT(DECODE(KND, '6', 1, NULL)) AS A06, COUNT(DECODE(KND, '7', 1, NULL)) AS A07 FROM GT_C059_A A WHERE A.CSS_NAM IS NOT NULL
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.FNE_TYPE IN #{item}
AND A.CLR IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
AND A.CSS_NAM LIKE '%' || #{cssNam} || '%'
GROUP BY ROLLUP(A.CSS_NAM) ORDER BY NAME NULLS FIRST ) TEMP)TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}

