SELECT
	DECODE(NAME, NULL, '합계', NAME) AS  "구분",
	TO_CHAR(TOTAL, '999,999,999,999') AS "합계",
	TO_CHAR(A01, '999,999,999,999') AS "전자",
	TO_CHAR(A02, '999,999,999,999') AS "일반",
	TO_CHAR(A03, '999,999,999,999') AS "신신호"
FROM (
	SELECT
		A.GU_NAM AS NAME,
		(COUNT(DECODE(CTL_MET, '1', 1, NULL)) + COUNT(DECODE(CTL_MET, '2', 1, NULL)) + COUNT(DECODE(CTL_MET, '3', 1, NULL))) AS TOTAL,
		COUNT(DECODE(CTL_MET, '1', 1, NULL)) AS A01,
		COUNT(DECODE(CTL_MET, '2', 1, NULL)) AS A02,
		COUNT(DECODE(CTL_MET, '3', 1, NULL)) AS A03
	FROM SMARTTGIS.GT_A061_P A



AND A.GU_CDE  IN 	#{item}


AND A.DONG_CDE  IN 	#{item}


AND A.PE_CDE  IN 	#{item}


AND A.CTL_MET  IN 	#{item}


AND A.CON_FOR  IN 	#{item}


AND A.LINE_MET  IN 	#{item}


AND A.ROD_KND  IN 	#{item}


AND A.ESB_YMD >= #{stYmd}


AND A.ESB_YMD <= #{enYmd}



	GROUP BY ROLLUP(A.GU_NAM)
	ORDER BY NAME NULLS FIRST) TEMP


    	SELECT
			DECODE(NAME, NULL, '합계', NAME) AS  "구분",
			TO_CHAR(TOTAL, '999,999,999,999') AS "합계",
			TO_CHAR(A01, '999,999,999,999') AS "전자",
			TO_CHAR(A02, '999,999,999,999') AS "일반",
			TO_CHAR(A03, '999,999,999,999') AS "신신호"
		FROM  (
		SELECT  A.PE_NAM AS NAME,
				(COUNT(DECODE(CTL_MET, '1', 1, NULL)) + COUNT(DECODE(CTL_MET, '2', 1, NULL)) + COUNT(DECODE(CTL_MET, '3', 1, NULL))) AS TOTAL,
				COUNT(DECODE(CTL_MET, '1', 1, NULL)) AS A01,
				COUNT(DECODE(CTL_MET, '2', 1, NULL)) AS A02,
				COUNT(DECODE(CTL_MET, '3', 1, NULL)) AS A03
		FROM SMARTTGIS.GT_A061_P A



		AND A.GU_CDE  IN 	#{item}


		AND A.DONG_CDE  IN 	#{item}


		AND A.PE_CDE  IN 	#{item}


		AND A.CTL_MET  IN 	#{item}


		AND A.CON_FOR  IN 	#{item}


		AND A.LINE_MET  IN 	#{item}


		AND A.ROD_KND  IN 	#{item}


		AND A.ESB_YMD >= #{stYmd}


		AND A.ESB_YMD <= #{enYmd}



		GROUP BY ROLLUP(A.PE_NAM)
		ORDER BY NAME NULLS FIRST) TEMP


SELECT
			DECODE(NAME, NULL, '합계', NAME) AS  "구분",
			TO_CHAR(TOTAL, '999,999,999,999') AS "합계",
			TO_CHAR(A01, '999,999,999,999') AS "전자",
			TO_CHAR(A02, '999,999,999,999') AS "일반",
			TO_CHAR(A03, '999,999,999,999') AS "신신호"
		FROM  (
	SELECT  G.EMD_KOR_NM AS NAME,
			(COUNT(DECODE(CTL_MET, '1', 1, NULL)) + COUNT(DECODE(CTL_MET, '2', 1, NULL)) + COUNT(DECODE(CTL_MET, '3', 1, NULL))) AS TOTAL,
			COUNT(DECODE(CTL_MET, '1', 1, NULL)) AS A01,
			COUNT(DECODE(CTL_MET, '2', 1, NULL)) AS A02,
			COUNT(DECODE(CTL_MET, '3', 1, NULL)) AS A03
	FROM SMARTTGIS.GT_A061_P A, SMARTTGIS.GT_LP_AA_EMD G
	WHERE A.DONG_NAM(+) = G.EMD_KOR_NM


	AND A.GU_CDE  IN 	#{item}


	AND A.DONG_CDE  IN 	#{item}


	AND A.PE_CDE  IN 	#{item}


	AND A.CTL_MET  IN 	#{item}


		AND A.CON_FOR  IN 	#{item}


		AND A.LINE_MET  IN 	#{item}


		AND A.ROD_KND  IN 	#{item}


	AND A.ESB_YMD >= #{stYmd}


	AND A.ESB_YMD <= #{enYmd}


	GROUP BY ROLLUP(G.EMD_KOR_NM)
	ORDER BY NAME NULLS FIRST) TEMP


		SELECT  TEMP.*
		FROM   (SELECT  ROWNUM  AS NUM,
		               TEMP.*,
		                COUNT(*) OVER() AS TOTALROWS
		        FROM   (SELECT
								 		MGRNU,
                                         PE_NAM,
                                         GU_NAM,
                                         DONG_NAM,
                                         JIBUN,
                                         CSS_NAM,
                                         TO_CODE('A061_P', 'CTL_MET',CTL_MET) AS CTL_MET_NAM,
		                                TO_CHAR(ESB_YMD ,'YYYY-MM-DD') AS ESB_YMD,
		                                 MK_MDL,
		                                 MK_CPY,
		                                 TO_CHAR(MK_YMD ,'YYYY-MM-DD') AS MK_YMD ,
		                                 MEA,
		                                 TO_CHAR(MEA_MKYMD ,'YYYY-MM-DD') AS MEA_MKYMD,
		                                 TO_CODE('A061_P', 'CON_FOR',CON_FOR) AS CON_FOR_NAM,
		                                 TO_CODE('A061_P', 'LINE_MET',LINE_MET) AS LINE_MET_NAM,
		                                 TO_CODE('A061_P', 'ROD_KND',ROD_KND) AS ROD_KND_NAM,
										XCE,
										YCE
						FROM	SMARTTGIS.GT_A061_P A



				AND A.GU_CDE  IN 	#{item}


				AND A.DONG_CDE  IN 	#{item}


				AND A.PE_CDE  IN 	#{item}


				AND A.CTL_MET  IN 	#{item}


				AND A.CON_FOR  IN 	#{item}


				AND A.LINE_MET  IN 	#{item}


				AND A.ROD_KND  IN 	#{item}


				AND A.ESB_YMD >= #{stYmd}


				AND A.ESB_YMD <= #{enYmd}



		                ORDER BY MGRNU DESC
		                ) TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}

SELECT ROWNUM AS NUM, TEMP.* FROM (SELECT MGRNU, PE_NAM, GU_NAM, DONG_NAM, JIBUN, CSS_NAM, TO_CODE('A061_P', 'CTL_MET',CTL_MET) AS CTL_MET_NAM, TO_CHAR(ESB_YMD ,'YYYY-MM-DD') AS ESB_YMD, MK_MDL, MK_CPY, TO_CHAR(MK_YMD ,'YYYY-MM-DD') AS MK_YMD , MEA, TO_CHAR(MEA_MKYMD ,'YYYY-MM-DD') AS MEA_MKYMD, TO_CODE('A061_P', 'CON_FOR',CON_FOR) AS CON_FOR_NAM, TO_CODE('A061_P', 'LINE_MET',LINE_MET) AS LINE_MET_NAM, TO_CODE('A061_P', 'ROD_KND',ROD_KND) AS ROD_KND_NAM, XCE, YCE FROM SMARTTGIS.GT_A061_P A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.CTL_MET IN #{item}
AND A.CON_FOR IN #{item}
AND A.LINE_MET IN #{item}
AND A.ROD_KND IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
ORDER BY MGRNU DESC) TEMP


SELECT  TEMP.*
		FROM   (SELECT  ROWNUM  AS NUM,
		               TEMP.*,
		                COUNT(*) OVER() AS TOTALROWS
		        FROM   (
				SELECT
					DECODE(NAME, NULL, '합계', NAME) AS  "구분",
					TO_CHAR(TOTAL, '999,999,999,999') AS "합계",
					TO_CHAR(A01, '999,999,999,999') AS "전자",
					TO_CHAR(A02, '999,999,999,999') AS "일반",
					TO_CHAR(A03, '999,999,999,999') AS "신신호"
				FROM (
					SELECT
						A.CSS_NAM AS NAME,
						(COUNT(DECODE(CTL_MET, '1', 1, NULL)) + COUNT(DECODE(CTL_MET, '2', 1, NULL)) + COUNT(DECODE(CTL_MET, '3', 1, NULL))) AS TOTAL,
						COUNT(DECODE(CTL_MET, '1', 1, NULL)) AS A01,
						COUNT(DECODE(CTL_MET, '2', 1, NULL)) AS A02,
						COUNT(DECODE(CTL_MET, '3', 1, NULL)) AS A03
					FROM SMARTTGIS.GT_A061_P A
					WHERE A.CSS_NAM IS NOT NULL


				AND A.GU_CDE  IN 	#{item}


				AND A.DONG_CDE  IN 	#{item}


				AND A.PE_CDE  IN 	#{item}


				AND A.CTL_MET  IN 	#{item}


				AND A.CON_FOR  IN 	#{item}


				AND A.LINE_MET  IN 	#{item}


				AND A.ROD_KND  IN 	#{item}


				AND A.ESB_YMD >= #{stYmd}


				AND A.ESB_YMD <= #{enYmd}


				AND A.CSS_NAM LIKE  '%' || #{cssNam} || '%'


					GROUP BY ROLLUP(A.CSS_NAM)
					ORDER BY NAME NULLS FIRST  ) TEMP)TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}
