SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "CCTV", TO_CHAR(A02, '999,999,999,999') AS "단속용", TO_CHAR(A03, '999,999,999,999') AS "불법주정차", TO_CHAR(A04, '999,999,999,999') AS "정보수집용", TO_CHAR(A05, '999,999,999,999') AS "방범용" FROM ( SELECT A.GU_NAM AS NAME, (COUNT(DECODE(KND, '0', 1, NULL)) + COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '0', 1, NULL)) AS A01, COUNT(DECODE(KND, '1', 1, NULL)) AS A02, COUNT(DECODE(KND, '2', 1, NULL)) AS A03, COUNT(DECODE(KND, '3', 1, NULL)) AS A04, COUNT(DECODE(KND, '4', 1, NULL)) AS A05 FROM SMARTTGIS.GT_A069_P A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.CNN_TYPE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(A.GU_NAM) ORDER BY NAME NULLS FIRST) TEMP

SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "CCTV", TO_CHAR(A02, '999,999,999,999') AS "단속용", TO_CHAR(A03, '999,999,999,999') AS "불법주정차", TO_CHAR(A04, '999,999,999,999') AS "정보수집용", TO_CHAR(A05, '999,999,999,999') AS "방범용" FROM ( SELECT A.PE_NAM AS NAME, (COUNT(DECODE(KND, '0', 1, NULL)) + COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '0', 1, NULL)) AS A01, COUNT(DECODE(KND, '1', 1, NULL)) AS A02, COUNT(DECODE(KND, '2', 1, NULL)) AS A03, COUNT(DECODE(KND, '3', 1, NULL)) AS A04, COUNT(DECODE(KND, '4', 1, NULL)) AS A05 FROM SMARTTGIS.GT_A069_P A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.CNN_TYPE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(A.PE_NAM) ORDER BY NAME NULLS FIRST) TEMP

SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "CCTV", TO_CHAR(A02, '999,999,999,999') AS "단속용", TO_CHAR(A03, '999,999,999,999') AS "불법주정차", TO_CHAR(A04, '999,999,999,999') AS "정보수집용", TO_CHAR(A05, '999,999,999,999') AS "방범용" FROM ( SELECT G.EMD_KOR_NM AS NAME, (COUNT(DECODE(KND, '0', 1, NULL)) + COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '0', 1, NULL)) AS A01, COUNT(DECODE(KND, '1', 1, NULL)) AS A02, COUNT(DECODE(KND, '2', 1, NULL)) AS A03, COUNT(DECODE(KND, '3', 1, NULL)) AS A04, COUNT(DECODE(KND, '4', 1, NULL)) AS A05 FROM SMARTTGIS.GT_A069_P A, SMARTTGIS.GT_LP_AA_EMD G WHERE A.DONG_NAM(+) = G.EMD_KOR_NM
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.CNN_TYPE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(G.EMD_KOR_NM) ORDER BY NAME NULLS FIRST) TEMP

SELECT TEMP.* FROM (SELECT ROWNUM AS NUM, TEMP.*, COUNT(*) OVER() AS TOTALROWS FROM (SELECT MGRNU, PE_NAM, GU_NAM, DONG_NAM, JIBUN, CSS_NAM, TO_CODE('A069_P','KND',KND) AS KND_NAM, MK_MDL, ESB_HIT, MK_CPY, TO_CODE('A069_P','CNN_TYPE',CNN_TYPE) AS CNN_NAM, CNN_SITE, CNN_MAIN, TO_CHAR(ESB_YMD ,'YYYY-MM-DD') AS ESB_YMD, XCE, YCE FROM SMARTTGIS.GT_A069_P A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.CNN_TYPE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
ORDER BY MGRNU DESC ) TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}

SELECT ROWNUM AS NUM, TEMP.* FROM (SELECT MGRNU, PE_NAM, GU_NAM, DONG_NAM, JIBUN, CSS_NAM, TO_CODE('A069_P','KND',KND) AS KND_NAM, MK_MDL, ESB_HIT, MK_CPY, TO_CODE('A069_P','CNN_TYPE',CNN_TYPE) AS CNN_NAM, CNN_SITE, CNN_MAIN, TO_CHAR(ESB_YMD ,'YYYY-MM-DD') AS ESB_YMD, XCE, YCE FROM SMARTTGIS.GT_A069_P A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.CNN_TYPE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
ORDER BY MGRNU DESC) TEMP

SELECT TEMP.* FROM (SELECT ROWNUM AS NUM, TEMP.*, COUNT(*) OVER() AS TOTALROWS FROM (SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "CCTV", TO_CHAR(A02, '999,999,999,999') AS "단속용", TO_CHAR(A03, '999,999,999,999') AS "불법주정차", TO_CHAR(A04, '999,999,999,999') AS "정보수집용", TO_CHAR(A05, '999,999,999,999') AS "방범용" FROM (SELECT A.CSS_NAM AS NAME, (COUNT(DECODE(KND, '0', 1, NULL)) + COUNT(DECODE(KND, '1', 1, NULL)) + COUNT(DECODE(KND, '2', 1, NULL)) + COUNT(DECODE(KND, '3', 1, NULL)) + COUNT(DECODE(KND, '4', 1, NULL))) AS TOTAL, COUNT(DECODE(KND, '0', 1, NULL)) AS A01, COUNT(DECODE(KND, '1', 1, NULL)) AS A02, COUNT(DECODE(KND, '2', 1, NULL)) AS A03, COUNT(DECODE(KND, '3', 1, NULL)) AS A04, COUNT(DECODE(KND, '4', 1, NULL)) AS A05 FROM SMARTTGIS.GT_A069_P A WHERE A.CSS_NAM IS NOT NULL
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.KND IN #{item}
AND A.CNN_TYPE IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
AND A.CSS_NAM LIKE '%' || #{cssNam} || '%'
GROUP BY ROLLUP(A.CSS_NAM) ORDER BY NAME NULLS FIRST ) TEMP)TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}
