SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "문자식", TO_CHAR(A02, '999,999,999,999') AS "도형식", TO_CHAR(A03, '999,999,999,999') AS "차로제어식" FROM ( SELECT A.GU_NAM AS NAME, (COUNT(DECODE(LHB_KND, '1', 1, NULL)) + COUNT(DECODE(LHB_KND, '2', 1, NULL)) + COUNT(DECODE(LHB_KND, '3', 1, NULL))) AS TOTAL, COUNT(DECODE(LHB_KND, '1', 1, NULL)) AS A01, COUNT(DECODE(LHB_KND, '2', 1, NULL)) AS A02, COUNT(DECODE(LHB_KND, '3', 1, NULL)) AS A03 FROM SMARTTGIS.GT_C097_P A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.LHB_KND IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(A.GU_NAM) ORDER BY NAME NULLS FIRST) TEMP

SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "문자식", TO_CHAR(A02, '999,999,999,999') AS "도형식", TO_CHAR(A03, '999,999,999,999') AS "차로제어식" FROM ( SELECT A.PE_NAM AS NAME, (COUNT(DECODE(LHB_KND, '1', 1, NULL)) + COUNT(DECODE(LHB_KND, '2', 1, NULL)) + COUNT(DECODE(LHB_KND, '3', 1, NULL))) AS TOTAL, COUNT(DECODE(LHB_KND, '1', 1, NULL)) AS A01, COUNT(DECODE(LHB_KND, '2', 1, NULL)) AS A02, COUNT(DECODE(LHB_KND, '3', 1, NULL)) AS A03 FROM SMARTTGIS.GT_C097_P A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.LHB_KND IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(A.PE_NAM) ORDER BY NAME NULLS FIRST) TEMP

SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "문자식", TO_CHAR(A02, '999,999,999,999') AS "도형식", TO_CHAR(A03, '999,999,999,999') AS "차로제어식" FROM ( SELECT G.EMD_KOR_NM AS NAME, (COUNT(DECODE(LHB_KND, '1', 1, NULL)) + COUNT(DECODE(LHB_KND, '2', 1, NULL)) + COUNT(DECODE(LHB_KND, '3', 1, NULL))) AS TOTAL, COUNT(DECODE(LHB_KND, '1', 1, NULL)) AS A01, COUNT(DECODE(LHB_KND, '2', 1, NULL)) AS A02, COUNT(DECODE(LHB_KND, '3', 1, NULL)) AS A03 FROM SMARTTGIS.GT_C097_P A, SMARTTGIS.GT_LP_AA_EMD G WHERE A.DONG_NAM(+) = G.EMD_KOR_NM
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.LHB_KND IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
GROUP BY ROLLUP(G.EMD_KOR_NM) ORDER BY NAME NULLS FIRST) TEMP


SELECT	TEMP.*
FROM	(SELECT	ROWNUM  AS NUM,
				TEMP.*,
				COUNT(*) OVER() AS TOTALROWS
        FROM   (SELECT
					MGRNU,
					PE_NAM,
					GU_NAM,
					DONG_NAM,
					JIBUN,
					CSS_NAM,
					TO_CODE('C097_P','LHB_KND',LHB_KND) AS LHB_KND_NAM,
					STD,
					TO_CHAR(ESB_YMD, 'YYYY-MM-DD') AS ESB_YMD,
					XCE,
					YCE
				FROM	SMARTTGIS.GT_C097_P A



		AND A.GU_CDE  IN 	#{item}


		AND A.DONG_CDE  IN 	#{item}


		AND A.PE_CDE  IN 	#{item}


		AND A.LHB_KND  IN 	#{item}


		AND A.ESB_YMD >= #{stYmd}


		AND A.ESB_YMD <= #{enYmd}



                ORDER BY MGRNU DESC
                ) TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}

SELECT ROWNUM AS NUM, TEMP.* FROM (SELECT MGRNU, PE_NAM, GU_NAM, DONG_NAM, JIBUN, CSS_NAM, TO_CODE('C097_P','LHB_KND',LHB_KND) AS LHB_KND_NAM, STD, TO_CHAR(ESB_YMD, 'YYYY-MM-DD') AS ESB_YMD, XCE, YCE FROM SMARTTGIS.GT_C097_P A
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.LHB_KND IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
ORDER BY MGRNU DESC) TEMP

SELECT TEMP.* FROM (SELECT ROWNUM AS NUM, TEMP.*, COUNT(*) OVER() AS TOTALROWS FROM (SELECT DECODE(NAME, NULL, '합계', NAME) AS "구분", TO_CHAR(TOTAL, '999,999,999,999') AS "합계", TO_CHAR(A01, '999,999,999,999') AS "문자식", TO_CHAR(A02, '999,999,999,999') AS "도형식", TO_CHAR(A03, '999,999,999,999') AS "차로제어식" FROM (SELECT A.CSS_NAM AS NAME, (COUNT(DECODE(LHB_KND, '1', 1, NULL)) + COUNT(DECODE(LHB_KND, '2', 1, NULL)) + COUNT(DECODE(LHB_KND, '3', 1, NULL))) AS TOTAL, COUNT(DECODE(LHB_KND, '1', 1, NULL)) AS A01, COUNT(DECODE(LHB_KND, '2', 1, NULL)) AS A02, COUNT(DECODE(LHB_KND, '3', 1, NULL)) AS A03 FROM SMARTTGIS.GT_C097_P A WHERE A.CSS_NAM IS NOT NULL
AND A.GU_CDE IN #{item}
AND A.DONG_CDE IN #{item}
AND A.PE_CDE IN #{item}
AND A.LHB_KND IN #{item}
AND A.ESB_YMD >= #{stYmd}
AND A.ESB_YMD <= #{enYmd}
AND A.CSS_NAM LIKE '%' || #{cssNam} || '%'
GROUP BY ROLLUP(A.CSS_NAM) ORDER BY NAME NULLS FIRST ) TEMP)TEMP)TEMP WHERE NUM BETWEEN #{rows} * ( #{page} - 1 ) + 1 AND #{rows}* #{page}
